import React, { Component } from "react";
import projeto1 from "../images/portfolio-images/thumbs/nomeProjeto_thumb.jpg";
import projeto2 from "../images/portfolio-images/thumbs/nomeProjeto_thumb.jpg";
import projeto3 from "../images/portfolio-images/thumbs/nomeProjeto_thumb.jpg";

class Portfolio extends Component {
  state = {};
  render() {
    const works = [
      {
        id: 1,
        title: "Roadcamper",
        substitle: "web responsive - 2018",
        image: { projeto1 },
        modal: "portfolioModal1"
      },
      {
        id: 2,
        title: "Inforarte",
        substitle: "web responsive - 2018",
        image: { projeto2 },
        modal: "portfolioModal2"
      },
      {
        id: 3,
        title: "Inforarte",
        substitle: "React.js - 2019",
        image: { projeto3 },
        modal: "portfolioModal3"
      }
    ];

    // const works = {
    //   title: ["Roadcamper", "Inforarte", "Portfolio"],
    //   substitle: [
    //     "web responsive - 2018",
    //     "web responsive - 2018",
    //     "React.js - 2019"
    //   ],
    //   image: [{ projeto1 }, { projeto2 }, { projeto3 }],
    //   modal: ["portfolioModal1", "portfolioModal2", "portfolioModal3"]
    // };

    const projects = works.map(work => {
      // this will this code for each word of the array menu
      return (
        <div className="content">
          <a
            className="portfolio-link"
            data-toggle="modal"
            href={"#" + this.work.modal}
          >
            <div className="content-overlay" />
            <img
              className="img-responsive portfolio-nopad"
              src={this.work.image}
              alt=""
            />
            <div className="content-details">
              <h3 className="content-title">{this.work.title}</h3>
              <p className="content-text">{this.work.substitle}</p>
            </div>
          </a>
        </div>
      );
    });
    return (
      <section className="">
        <div className="container-fuild">
          <div className="row">
            <div className="col-lg-12">
              <h1 className="h1-margin portfolio">
                <i className="icon waves">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    xmlnsXlink="http://www.w3.org/1999/xlink"
                    version="1.1"
                    id="Camada_1"
                    x="0px"
                    y="0px"
                    viewBox="0 0 54 12"
                    style={{ enableBackground: "new 0 0 54 12" }}
                    xmlSpace="preserve"
                  >
                    <style
                      type="text/css"
                      dangerouslySetInnerHTML={{
                        __html:
                          "\n\t.st0{fill:none;stroke:#D85D5D;stroke-width:6;}\n"
                      }}
                    />
                    <path
                      id="Caminho_2"
                      className="st0"
                      d="M2,8.29l7.66-4.58l7.01,4.58l9.81-4.58l9.34,4.58l8.13-4.58L52,8.29"
                    />
                  </svg>
                </i>
                <span className="title-first">
                  Port <br />
                  <span className="title-second">
                    folio <span className="title-bar">/</span>
                  </span>
                </span>
              </h1>
            </div>
          </div>
          <div className="row">
            <div className="col-lg-4 col-sm-4 col-xs-6 portfolio-item ">
              {projects}
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default Portfolio;
