import React, { Component } from "react";
import projeto1 from "../images/portfolio-images/thumbs/teste.jpg";
import projeto2 from "../images/portfolio-images/thumbs/teste.jpg";
import projeto3 from "../images/portfolio-images/thumbs/teste.jpg";
import Modal from "react-modal";

class Portfolio extends Component {
  constructor() {
    super();
    this.state = {
      isActive: false
    };

    this.toggleModal = this.toggleModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
  }
  componentWillMount() {
    Modal.setAppElement("body");
  }

  toggleModal = () => {
    this.setState({
      isActive: !this.state.isActive
    });
  };

  closeModal() {
    this.setState({ modalIsOpen: false });
  }

  render() {
    const works = [
      {
        id: 1,
        title: "Roadcamper",
        substitle: "web responsive - 2018",
        image: { projeto1 },
        modal: "portfolioModal1"
      },
      {
        id: 2,
        title: "Inforarte",
        substitle: "web responsive - 2018",
        image: { projeto2 },
        modal: "portfolioModal2"
      },
      {
        id: 3,
        title: "Inforarte",
        substitle: "React.js - 2019",
        image: { projeto3 },
        modal: "portfolioModal3"
      }
    ];
    const projects = works.map(work => {
      // this will this code for each word of the array menu
      return (
        <div className="col-lg-4 col-sm-12 col-xs-12 " key={work.id}>
          <div className="conteudo">
            <button
              className=""
              onClick={this.toggleModal}
              href={"#" + work.modal}
            >
              <div className="conteudo-overlay" />
              <img className="img-fluid" src={work.image} alt="" />
              <div className="project-details fadeIn-bottom">
                <i className="shape">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    xmlnsXlink="http://www.w3.org/1999/xlink"
                    version="1.1"
                    id="Camada_1"
                    x="0px"
                    y="0px"
                    viewBox="0 0 600 500"
                    style={{ enableBackground: "new 0 0 600 500" }}
                    xmlSpace="preserve"
                  >
                    <style
                      type="text/css"
                      dangerouslySetInnerHTML={{
                        __html: "\n\t.st0{fill:#D85D5D;}\n"
                      }}
                    />
                    <path
                      id="Caminho_8"
                      className="st0"
                      d="M600,500V152L0,361.7V500H600z"
                    />
                  </svg>
                </i>
                <h3 className="project-title">{work.title}</h3>
                <p className="project-subtitle">{work.substitle}</p>
                <p className="project-see">See project</p>
              </div>
            </button>
          </div>

          <Modal
            key={work.id}
            isOpen={this.state.isActive}
            onRequestClose={this.toggleModal}
            id={"#" + work.modal}
          >
            <div className="section">
              <div className="container-fluid">
                {/* close modal */}
                <div className="row">
                  <div className="col-lg-12 text-right">
                    <button onClick={this.toggleModal}>close</button>
                  </div>
                </div>

                {/* modal title */}
                <div className="row">
                  <div className="col-lg-12">
                    <h2 className="h2-margin">
                      <i className="icon waves">
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          xmlnsXlink="http://www.w3.org/1999/xlink"
                          version="1.1"
                          id="Camada_1"
                          x="0px"
                          y="0px"
                          viewBox="0 0 54 12"
                          style={{ enableBackground: "new 0 0 54 12" }}
                          xmlSpace="preserve"
                        >
                          <style
                            type="text/css"
                            dangerouslySetInnerHTML={{
                              __html:
                                "\n\t.st0{fill:none;stroke:#D85D5D;stroke-width:6;}\n"
                            }}
                          />
                          <path
                            id="Caminho_2"
                            className="st0"
                            d="M2,8.29l7.66-4.58l7.01,4.58l9.81-4.58l9.34,4.58l8.13-4.58L52,8.29"
                          />
                        </svg>
                      </i>
                      <span className="modal-title">
                        {work.title} <span className="modal-title-bar">/</span>
                      </span>
                    </h2>
                  </div>
                </div>
              </div>
            </div>

            <h2>{work.title}</h2>

            <div>{work.substitle}</div>
          </Modal>
        </div>
      );
    });

    return (
      <section className="">
        <div className="container-fuild">
          <div className="row">
            <div className="col-lg-12">
              <h1 className="h1-margin portfolio">
                <i className="icon waves">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    xmlnsXlink="http://www.w3.org/1999/xlink"
                    version="1.1"
                    id="Camada_1"
                    x="0px"
                    y="0px"
                    viewBox="0 0 54 12"
                    style={{ enableBackground: "new 0 0 54 12" }}
                    xmlSpace="preserve"
                  >
                    <style
                      type="text/css"
                      dangerouslySetInnerHTML={{
                        __html:
                          "\n\t.st0{fill:none;stroke:#D85D5D;stroke-width:6;}\n"
                      }}
                    />
                    <path
                      id="Caminho_2"
                      className="st0"
                      d="M2,8.29l7.66-4.58l7.01,4.58l9.81-4.58l9.34,4.58l8.13-4.58L52,8.29"
                    />
                  </svg>
                </i>
                <span className="title-first">
                  Port <br />
                  <span className="title-second">
                    folio <span className="title-bar">/</span>
                  </span>
                </span>
              </h1>
            </div>
          </div>

          <div className="row">{projects} </div>
        </div>
      </section>
    );
  }
}

export default Portfolio;
