import React, { Component } from "react";

class Portfolio extends Component {
  state = {};
  render() {
    const works = {
      title: ["Roadcamper", "Inforarte", "Portfolio"],
      substitle: [
        "web responsive - 2018",
        "web responsive - 2018",
        "React.js - 2019"
      ],
      image: ["Home", "About me", "Portfolio"]
    };
    // const projects = works.map(work => {
    //   // this will this code for each word of the array menu
    //   return (
    //     <li className="nav-item">
    //       <a className="page-link" href={"#" + work}>
    //         <span className="hover-line">
    //           <span>{work}</span>
    //         </span>
    //       </a>
    //     </li>
    //   );
    // });
    return (
      <section className="">
        <div className="container-fuild">
          <div className="row">
            <div className="col-lg-12">
              <h1 className="h1-margin portfolio">
                <i className="icon waves">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    xmlnsXlink="http://www.w3.org/1999/xlink"
                    version="1.1"
                    id="Camada_1"
                    x="0px"
                    y="0px"
                    viewBox="0 0 54 12"
                    style={{ enableBackground: "new 0 0 54 12" }}
                    xmlSpace="preserve"
                  >
                    <style
                      type="text/css"
                      dangerouslySetInnerHTML={{
                        __html:
                          "\n\t.st0{fill:none;stroke:#D85D5D;stroke-width:6;}\n"
                      }}
                    />
                    <path
                      id="Caminho_2"
                      className="st0"
                      d="M2,8.29l7.66-4.58l7.01,4.58l9.81-4.58l9.34,4.58l8.13-4.58L52,8.29"
                    />
                  </svg>
                </i>
                <span className="title-first">
                  Port <br />
                  <span className="title-second">
                    folio <span className="title-bar">/</span>
                  </span>
                </span>
              </h1>
            </div>
          </div>
          <div className="row">
            <div className="col-lg-4 col-sm-4 col-xs-6 portfolio-item ">
              <div className="content">
                <a
                  className="portfolio-link"
                  data-toggle="modal"
                  href="#portfolioModal1"
                >
                  <img
                    className="img-responsive portfolio-nopad"
                    src="./images/portfolio-images/thumbs/nomeProjeto_thumb.jpg"
                    alt=""
                  />
                  <div className="content-overlay" />

                  <div className="content-details">
                    <h3 className="content-title">Sistema Sanitário</h3>
                    <p className="content-text">Sistema de Água</p>
                  </div>
                </a>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default Portfolio;
